// 最小スタブ: CSPに従い外部CDNを使わず、Chart未定義エラーを防ぐ
// 実際のグラフ描画は行いませんが、API呼び出しでエラーにならないようにします。
(function(){
  function NoopChart(ctx, config){ this.ctx = ctx; this.config = config || {}; }
  NoopChart.prototype.destroy = function(){};
  // グローバルに公開
  if (typeof window !== 'undefined') window.Chart = NoopChart;
})();